const $points = {}

function Dijkstra(grid) {
    console.log(grid)
    const nodes = getNodes(grid)
    console.log(nodes)
    console.log($points)
    getNeighbors(grid, $points.startNode)
}

//function to get neighbors by node
function getNeighbors(grid, node) {
    console.log(node)
    const nodeNeighbors = {
        nodeLeft: {},
        nodeRight: {},
        nodeUp: {},
        nodeDown: {}
    }
    if(node !== undefined || null) {
        console.log(node.col)
        nodeNeighbors.nodeRight = grid[node.col+1][node.row]
        nodeNeighbors.nodeLeft = grid[node.col-1][node.row]
        nodeNeighbors.nodeUp = grid[node.col][node.row+1]
        nodeNeighbors.nodeDown = grid[node.col][node.row-1]

    }
}

function getPoints(node) {
    if(node.startNode) {
        $points.startNode = node
    }
    else if(node.endNode) {
        $points.endNode = node
    }
    return $points
}

function getNodes(grid) {
    const nodes = []
    for (let row of grid) {
        const currRow = []
        for(var node of row) {
            if(node.startNode) {
                node.nodeDistance = 0
                getPoints(node)
            }
            else if(node.endNode) {
                getPoints(node)
            }
            currRow.push(node)
        }
        nodes.push(currRow)
    }
    return nodes 
}

export default Dijkstra
