const $points = {}

function Dijkstra(grid) {
    console.log(grid)
    const nodes = getNodes(grid)
    console.log(nodes)
    console.log($points)
    return getNeighbors(grid, $points.startNode)
}

//function to get neighbors by node
function getNeighbors(grid, node) {
    console.log(node)
    var nodeNeighbors = {}
    if(node !== undefined || null) {
        console.log(node.col)
        nodeNeighbors.nodeRight = grid[node.row][node.col+1]
        nodeNeighbors.nodeLeft = grid[node.row][node.col-1]
        nodeNeighbors.nodeUp = grid[node.row-1][node.col]
        nodeNeighbors.nodeDown = grid[node.row+1][node.col]

    }
    console.log(nodeNeighbors)
    return nodeNeighbors
}

function getPoints(node) {
    if(node.startNode) {
        $points.startNode = node
    }
    else if(node.endNode) {
        $points.endNode = node
    }
    return $points
}

function getNodes(grid) {
    const nodes = []
    for (let row of grid) {
        const currRow = []
        for(var node of row) {
            if(node.startNode) {
                node.nodeDistance = 0
                getPoints(node)
            }
            else if(node.endNode) {
                getPoints(node)
            }
            currRow.push(node)
        }
        nodes.push(currRow)
    }
    return nodes 
}

export default Dijkstra
