import React from 'react'
import Node from './Component/node'
import Dijkstra from '../dijkstras'


export default class PathFinder extends React.Component {

    state = {
        disktra: []
    } 


    mountDijkstra = () => {
        Dijkstra(this.state.grid)
    }

    componentDidMount() {
        const grid = [] 
        for(let row = 0; row <= 15; row++) {
            const curRow = []
            for(let col =0; col <=40; col++) {
                const currentNode = {
                    row,
                    col,
                    startNode: row == 10 && col == 5,
                    endNode: row == 10 && col == 30
                }
                curRow.push(currentNode)
            }
            nodes.push(curRow)
        }
        this.setState({grid})
    }


    styles = {
        grid: {
            position: 'absolute'
        }
    }


    render() {
        this.mountDijkstra()
        console.log(this.state)
        const {nodes} = this.state
        console.log(nodes)
        return (
            <div style={this.styles.grid}>
                {nodes.map((row, rowIdx) => {
                    return <div>
                        {row.map((node, nodeIdx) => <Node nodeInfo={node}/>)}
                    </div>
                })}
            </div>
        )
    }
}
