const $points = {}

function Dijkstra(grid) {
    console.log(grid)
    const nodes = getNodes(grid)
    console.log(nodes)
    console.log($points)
    getNeighbors(grid, $points.startNode)
}

//function to get neighbors by node
function getNeighbors(grid, node) {
    console.log(node)
    console.log(grid[node.col][node.row])
}

function getPoints(node) {
    if(node.startNode == true) {
        $points.startNode = node
    }
    else if(node.endNode == true) {
        $points.endNode = node
    }
    return $points
}

function getNodes(grid) {
    const nodes = []
    for (let row of grid) {
        const currRow = []
        for(var node of row) {
            if(node.startNode) {
                node.nodeDistance = 0
                getPoints(node)
            }
            else if(node.endNode) {
                getPoints(node)
            }
            currRow.push(node)
        }
        nodes.push(currRow)
    }
    return nodes 
}

export default Dijkstra
